import React from 'react'

function CartItem({id, name, price, qty, updateQty}) {
    const addOne = () => {
        updateQty(id, qty+1);
    }
    const subOne = () => {
        updateQty(id, qty-1);
    }

    return(
        <div className = "Cart">
            <div>{name}</div>
            <div>${price}</div>
            <div>
                <button onClick={addOne}>-</button>
                {qty}
                <button onClick={subOne}>+</button>
            </div>
            <div>Total: ${qty*price}</div>
        </div>
    );

}

export default CartItem;
