import React, { useState } from "react"


function MoodToggler () {
    const [isHappy, setIsHappy] = useState(true);
    return (
            <h3 style={{backgroundColor: isHappy ? 'green' : 'red'}} onClick={() => setIsHappy(!isHappy)}>{isHappy ? ":)" : ":("}</h3>
    );

}

export default MoodToggler;
